mapper.select:
select
b.*
from (
select rownum rn, a.*
from (
select * from ${questions}
where ${numHotel} = #{numHotel} and ${numAnswer} = #{numAnswer}

)a
)b order by rn desc

select c.* from(
select
rownum bn, b.*
from (
select rownum rn, a.*
from (
select * from
${questions}
where ${numHotel} = #{numHotel} and ${numAnswer} = 1

)a order by rn desc
)b
)c where bn between #{start} and #{end}
